import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Importando el hook useNavigate

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();  // Usamos el hook useNavigate dentro del componente

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Comprobamos las credenciales
    if (email === 'hola@hola.com' && password === 'hola') {
      navigate('/home');  // Si las credenciales son correctas, redirigimos a /home
    } else {
      alert('Credenciales incorrectas');
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar sesi칩n</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Correo electr칩nico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contrase침a"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Iniciar sesi칩n</button>
      </form>
    </div>
  );
}

export default Login;
