import React, { useState } from 'react';
import Login from './screen/Login';
import Register from './screen/Register';

function App() {
  const [isRegistering, setIsRegistering] = useState(false);

  const handleRegisterClick = () => {
    setIsRegistering(true);  // Cambia el estado para mostrar Register
  };

  const handleLoginClick = () => {
    setIsRegistering(false);  // Cambia el estado para mostrar Login
  };

  return (
    <div className="App">
      <h1>{isRegistering ? 'Register' : 'Login'}</h1>

      {isRegistering ? (
        <Register handleLoginClick={handleLoginClick} />
      ) : (
        <Login handleRegisterClick={handleRegisterClick} />
      )}
    </div>
  );
}

export default App;
