import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Login from './screen/Login';
import Register from './screen/Register';
import Home from './screen/Home';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate(); // Esto solo se puede usar dentro de Router

  // Función para manejar el login
  const handleLogin = (email, password) => {
    // Simulamos una validación de usuario (reemplazar con lógica real de autenticación)
    if (email === 'usuario@ejemplo.com' && password === 'contraseña123') {
      setIsAuthenticated(true);
      navigate('/home');  // Redirigir a la página Home
    } else {
      alert('Credenciales incorrectas');
    }
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/home" element={isAuthenticated ? <Home /> : <Login />} />
      </Routes>
    </Router>
  );
}

export default App;
