// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './screen/Login';
import Register from './screen/Register';
import Home from './screen/Home'; // Cambié la ruta a 'screen/Home'

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleRegister = () => {
    setIsAuthenticated(true);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<Login handleRegisterClick={() => setIsAuthenticated(false)} />}
        />
        <Route
          path="/register"
          element={<Register handleLoginClick={() => setIsAuthenticated(false)} />}
        />
        {/* Página Home, solo accesible si el usuario está autenticado */}
        <Route
          path="/home"
          element={isAuthenticated ? <Home /> : <Login />}
        />
      </Routes>
    </Router>
  );
}

export default App;
