import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';
import { db } from '../firebase'; // Asegúrate de importar la instancia de Firebase.
import { collection, getDocs } from 'firebase/firestore';

function Home() {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    const fetchVideos = async () => {
      const videoCollection = collection(db, 'videos');
      const videoSnapshot = await getDocs(videoCollection);
      
      // Aquí agregamos el ID de cada documento a los datos del video
      const videoList = videoSnapshot.docs.map(doc => ({
        id: doc.id,  // Agregamos el ID del documento
        ...doc.data()  // Mantenemos los demás datos del video
      }));
      
      setVideos(videoList);
    };

    fetchVideos();
  }, []);

  return (
    <div className="home-container">
      <h2>Lista de Videos</h2>
      
      {/* Botón para agregar un nuevo video */}
      <Link to="/add-video" className="add-video-btn">
        Añadir Video
      </Link>
      
      <div className="video-list">
        {videos.map((video) => (
          <div key={video.id} className="video-card">
            <img src={video.thumbnail} alt={video.title} className="video-thumbnail" />
            <h3>{video.title}</h3>
            {/* Aquí se pasa el video.id correctamente en la URL */}
            <Link to={`/video/${video.id}`} className="video-link">
              Ver Video
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
